import{_ as t,a as r,o as a,ag as i}from"./chunks/framework.C2qfZH9T.js";const s="/assets/1-seller-profile-page.BZzrP3Rl.png",o="/assets/2-write-a-review.aZt70MNm.png",n="/assets/3-review-msg.ClAtulJ8.png",l="/assets/4-review-unapproved-state.BwzlkO7S.png",p="/assets/5-select-action.DH3lddxH.png",c="/assets/6-approved.C-wmyW2e.png",d="/assets/7-supplier-review.CBjEvKJx.png",h="/assets/8-review-at-frontend.w9U1l_qc.png",k=JSON.parse('{"title":"B2B Marketplace Review","description":"","frontmatter":{},"headers":[],"relativePath":"b2b-marketplace/b2b-marketplace-review.md","filePath":"b2b-marketplace/b2b-marketplace-review.md","lastUpdated":1758287359000}'),w={name:"b2b-marketplace/b2b-marketplace-review.md"};function v(m,e,g,u,b,_){return a(),r("div",null,e[0]||(e[0]=[i('<h1 id="b2b-marketplace-review" tabindex="-1">B2B Marketplace Review <a class="header-anchor" href="#b2b-marketplace-review" aria-label="Permalink to &quot;B2B Marketplace Review&quot;">​</a></h1><p>The <strong>Review</strong> feature allows buyers to share their experience with sellers in the marketplace. It is designed to build trust and make buying decisions easier.</p><ul><li>Buyers can rate sellers based on service quality, communication, and reliability.</li><li>Reviews help other buyers choose the right seller with confidence.</li><li>Sellers can use reviews to gain credibility and improve their services.</li></ul><p>This feature creates a transparent B2B marketplace where genuine feedback supports better business relationships.</p><p><strong>Note:</strong> Reviews can only be given by logged-in customers after their product has been successfully delivered.</p><hr><h3 id="how-to-submit-a-review" tabindex="-1">How to Submit a Review <a class="header-anchor" href="#how-to-submit-a-review" aria-label="Permalink to &quot;How to Submit a Review&quot;">​</a></h3><p>The customer opens the <strong>Supplier Page</strong> to view details about the seller. From this page, they can click on the <strong>“Review”</strong> button.</p><img src="'+s+'" alt="Seller Profile Page"><p>Once the <strong>Review Page</strong> is open, click on the <strong>“Write a Review”</strong> button to start adding your feedback about the seller.</p><img src="'+o+'" alt="Write a Review Button"><p>A popup box will open on your screen.</p><ul><li><p><strong>Rating:</strong> Select the rating the customer wants to give the seller.</p></li><li><p><strong>Title:</strong> Enter a clear title for the review.</p></li><li><p><strong>Comment:</strong> Write detailed comments about the seller’s service.</p></li></ul><p>Finally, click on the <strong>“Submit”</strong> button to post the review.</p><img src="'+n+'" alt="Review Submission Popup"><p><strong>Note:</strong> Reviews show on the page only after the admin approves.</p><hr><h3 id="admin-panel-approve-reviews" tabindex="-1">Admin Panel: Approve Reviews <a class="header-anchor" href="#admin-panel-approve-reviews" aria-label="Permalink to &quot;Admin Panel: Approve Reviews&quot;">​</a></h3><p>Go to <strong>B2B Marketplace &gt;&gt; Supplier Reviews</strong> to handle and approve supplier reviews.</p><p>The seller&#39;s review page will open, where the admin can see all customer ratings and feedback in one place.</p><img src="'+l+'" alt="Review Unapproved State"><p>To approve or disapprove a review, the admin selects it using the checkbox and chooses the desired action from the <strong>“Select Action”</strong> dropdown.</p><img src="'+p+'" alt="Select Action Dropdown"><p>The admin can then either approve or disapprove the review.</p><hr><h3 id="admin-supplier-reviews-page" tabindex="-1">Admin Supplier Reviews Page <a class="header-anchor" href="#admin-supplier-reviews-page" aria-label="Permalink to &quot;Admin Supplier Reviews Page&quot;">​</a></h3><p>Below is a detailed view of admin supplier reviews.</p><img src="'+c+'" alt="Admin Approved Supplier Reviews"><hr><h3 id="seller-reviews-page" tabindex="-1">Seller Reviews Page <a class="header-anchor" href="#seller-reviews-page" aria-label="Permalink to &quot;Seller Reviews Page&quot;">​</a></h3><p>Here’s a detailed view of seller reviews for easy monitoring.</p><img src="'+d+'" alt="Supplier Review Page"><hr><h3 id="frontend-view" tabindex="-1">Frontend View <a class="header-anchor" href="#frontend-view" aria-label="Permalink to &quot;Frontend View&quot;">​</a></h3><p>The display below shows how reviews appear to customers.</p><img src="'+h+'" alt="Review Frontend View">',36)]))}const R=t(w,[["render",v]]);export{k as __pageData,R as default};
