import{_ as t,a as o,o as r,ag as s}from"./chunks/framework.C2qfZH9T.js";const n="/assets/1-guest-sign-in.B9UOANwg.png",a="/assets/2-customer-login.CCDlXU-7.png",i="/assets/3-customer-profile-page.hTGjp8BE.png",u="/assets/4-quote-info.BSn-clK6.png",l="/assets/5-contact-info.BGrRGws2.png",p="/assets/6-product-info.B9gxiJJi.png",c="/assets/7-popup-rfq.CH7Pvw4s.png",d="/assets/8-redirect-page.CgCg0-s9.png",g="/assets/9-supplier-responsenew.C4Y5-xlV.png",m="/assets/10-approved-last-quotes-button.BSlEGzHu.png",h="/assets/11-quotes-confirm-by-customer.BWFgRf6P.png",f="/assets/12-buying-leads-seller.j-_F2TI9.png",q="/assets/13-send-quote-request-to-customer.CW656ivJ.png",b="/assets/14-req-ans-by-seller.B6Zkbq2S.png",w=JSON.parse('{"title":"Request for Quotes","description":"","frontmatter":{},"headers":[],"relativePath":"b2b-marketplace/request-for-quotes.md","filePath":"b2b-marketplace/request-for-quotes.md","lastUpdated":1758280276000}'),_={name:"b2b-marketplace/request-for-quotes.md"};function Q(C,e,k,y,R,P){return r(),o("div",null,e[0]||(e[0]=[s('<h1 id="request-for-quotes" tabindex="-1">Request for Quotes <a class="header-anchor" href="#request-for-quotes" aria-label="Permalink to &quot;Request for Quotes&quot;">​</a></h1><p><strong>Request for Quotes (RFQ)</strong> in a B2B marketplace helps buyers ask sellers for prices before placing an order. Instead of placing an order directly, buyers can send a detailed quote request with their requirements, and sellers respond with tailored offers. This makes it easier to compare prices, negotiate, and choose the right deal.</p><p><strong>Note:</strong> The RFQ feature can only be used by signed-in customers.</p><h3 id="create-request-for-quotes" tabindex="-1">Create Request for Quotes <a class="header-anchor" href="#create-request-for-quotes" aria-label="Permalink to &quot;Create Request for Quotes&quot;">​</a></h3><p>To begin using the RFQ feature, customers need to sign in to their account. Just click on the profile icon at the top right corner of the page.</p><img src="'+n+'" alt="Guest Sign In"><p>Enter email and password details, then click on the <strong>Sign In</strong> button.</p><img src="'+a+'" alt="Customer Login"><p>Customers can find the <strong>‘Request for Quote’</strong> option in the website header. Click on <strong>‘Request for Quote’</strong> to open the Request for Quote page.</p><img src="'+i+'" alt="Customer Profile Page"><p>This feature enables customers to submit their requirements and request a personalized price offer. Required details must be filled out before sending the quote.</p><h3 id="quote-information" tabindex="-1">Quote Information <a class="header-anchor" href="#quote-information" aria-label="Permalink to &quot;Quote Information&quot;">​</a></h3><ul><li><p><strong>Quote Title:</strong> A short name for your request.</p></li><li><p><strong>Quote Description:</strong> A clear explanation of what you need.</p></li></ul><img src="'+u+'" alt="Quote Information"><h3 id="contact-information" tabindex="-1">Contact Information <a class="header-anchor" href="#contact-information" aria-label="Permalink to &quot;Contact Information&quot;">​</a></h3><ul><li><p><strong>Name:</strong> Enter full name for identification.</p></li><li><p><strong>Company Name:</strong> Provide the name of the organization.</p></li><li><p><strong>Address:</strong> Mention the complete organization address.</p></li><li><p><strong>Contact Number:</strong> Add a valid phone number for communication.</p></li><li><p><strong>Add Attachment:</strong> Upload files like product specs or design references.</p></li><li><p><strong>Action Category:</strong> Select at least one Action Category.</p></li></ul><img src="'+l+'" alt="Contact Information"><h3 id="product-information" tabindex="-1">Product Information <a class="header-anchor" href="#product-information" aria-label="Permalink to &quot;Product Information&quot;">​</a></h3><p>To include product details in a quote, click on the <strong>Add Product</strong> button and fill in the required information.</p><img src="'+p+'" alt="Product Information"><p>A pop-up window will appear where the customer can enter product details such as:</p><ul><li><p><strong>Product Name:</strong> Specify the product you want to request.</p></li><li><p><strong>Product Description:</strong> Add important details for the product.</p></li><li><p><strong>Product Quantity:</strong> Enter the total number of units needed.</p></li><li><p><strong>Expected Price Per Quantity:</strong> Mention the price you expect per unit.</p></li><li><p><strong>Is Samples:</strong> Choose if the product needs a sample or not.</p></li><li><p><strong>Image:</strong> Enter an image for reference.</p></li></ul><p>After entering all the details, click the <strong>Submit</strong> button to confirm and add your product information.</p><img src="'+c+'" alt="Product Information Popup"><p>When all requirements are filled, go to the top right corner and click the <strong>‘Request for Quotes’</strong> button.</p><p>Customers are redirected to <strong>‘My Requested Quotes’</strong> after raising a request, where all their earlier requests are listed.</p><img src="'+d+'" alt="Redirect to My Requested Quotes"><p>The customer can now select a specific request they raised and view the supplier’s response. Click the <strong>Action</strong> icon to open the <strong>Quote Request</strong> page.</p><img src="'+g+'" alt="Supplier Response"><p>Customers can view details such as the <strong>Quote Request</strong> and the <strong>Quote Summary</strong> for quick reference. Customers have the option to either accept or reject a quotation after reviewing the seller’s response.</p><img src="'+m+'" alt="Approve Last Quote Button"><p>Click on <strong>“Approve Last Quote”</strong> to confirm the quotation. Once approved, the quotation status changes to <strong>Confirmed</strong>. This ensures the agreed terms are locked, preventing further changes.</p><img src="'+h+'" alt="Quotes Confirmed by Customer"><h3 id="seller-dashboard" tabindex="-1">Seller Dashboard <a class="header-anchor" href="#seller-dashboard" aria-label="Permalink to &quot;Seller Dashboard&quot;">​</a></h3><p>In the <strong>Seller Dashboard</strong>, click on <strong>Buying Leads</strong>. The seller will find the details of the RFQs here.</p><img src="'+f+'" alt="Buying Leads in Seller Dashboard"><p>Click the <strong>‘Send Quotes’</strong> button to send a quote to the customer.</p><p>The <strong>‘Send Quote Request’</strong> page will open. Enter the required details, including <strong>Quote Quantity, RFQ Quote Price per Quantity, Sample Information, and Shipping Information</strong>, then click the <strong>‘Send’</strong> button.</p><img src="'+q+'" alt="Send Quote Request to Customer"><p>After sending a quote, sellers are redirected to the <strong>‘Request For Quote’</strong> page, where sellers can view all details, including new, pending, answered, confirmed, and rejected.</p><img src="'+b+'" alt="Request for Quote Answers by Seller">',41)]))}const A=t(_,[["render",Q]]);export{w as __pageData,A as default};
