import{_ as e,a as o,o as n,ag as i}from"./chunks/framework.C2qfZH9T.js";const s="/assets/design.DWeI3EAR.png",r="/assets/designOutput.CywIliB8.png",a="/assets/designOutput2.QhvTh_qW.png",g="/assets/catAdmin.C7igJjs9.png",l="/assets/catSide.Nt5pB4hu.png",v=JSON.parse('{"title":"Design","description":"","frontmatter":{},"headers":[],"relativePath":"configure/design.md","filePath":"configure/design.md","lastUpdated":1770637971000}'),d={name:"configure/design.md"};function p(c,t,u,m,h,f){return n(),o("div",null,t[0]||(t[0]=[i('<h1 id="design" tabindex="-1">Design <a class="header-anchor" href="#design" aria-label="Permalink to &quot;Design&quot;">​</a></h1><p><strong>Admin Logo &amp; Favicon Configuration</strong></p><p>The <strong>Admin Logo</strong> is a distinctive image or emblem representing the administration interface of a system or website. It is often customizable to match your brand identity. Similarly, the <strong>Favicon</strong> is the small icon displayed in browser tabs, helping users visually identify your site.</p><p><strong>Steps to Update Admin Logo &amp; Favicon</strong></p><p>To update the <strong>Admin Logo</strong> and <strong>Favicon</strong> in Bagisto:</p><ol><li>Go to the <strong>Admin Panel</strong>.</li><li>Navigate to <strong>Configure → Design</strong>.</li><li>Under the <strong>Design</strong> section, you will find options to upload a new <strong>Admin Logo</strong> and <strong>Favicon</strong>.</li><li>Click on the <strong>Choose Files</strong> button to select the desired image files from your system.</li><li>Once selected, click the <strong>Save Configuration</strong> button to apply the changes.</li></ol><p><strong>Design Configuration Interface</strong></p><img src="'+s+'" alt="Design Configuration"><p><strong>Updated Logo &amp; Favicon Output</strong></p><p>After saving the configuration, the new <strong>Admin Logo</strong> and <strong>Favicon</strong> will be visible in the admin panel interface.</p><p><strong>Logo</strong><img src="'+r+'" alt="Updated Admin Logo"></p><p><strong>Favicon</strong><img src="'+a+'" alt="Updated Favicon"></p><p><strong>Menu Category View</strong></p><p>Bagisto v2.3.x introduced a new option to configure how product categories are displayed in the storefront menu.</p><p>You can now choose between:</p><ul><li><strong>Default Menu</strong>: Displays categories in the top navigation bar.</li><li><strong>Sidebar Menu</strong>: Displays categories in a vertical side panel, ideal for stores with many categories or subcategories.</li></ul><h3 id="to-update-the-menu-category-view" tabindex="-1">To update the Menu Category View: <a class="header-anchor" href="#to-update-the-menu-category-view" aria-label="Permalink to &quot;To update the Menu Category View:&quot;">​</a></h3><ol><li>Navigate to <strong>Admin Panel → Configure → Design</strong>.</li><li>Scroll down to the <strong>Header Settings</strong> section.</li><li>Under <strong>Category Menu View</strong>, select: <ul><li><code>Default Menu</code> or</li><li><code>Sidebar Menu</code> from the dropdown.</li></ul></li><li>Click <strong>Save Configuration</strong>.</li></ol><img src="'+g+'" alt="Menu Category View Setting"><p><strong>Shopfront view</strong></p><img src="'+l+'" alt="Shopfront Menu Category View">',21)]))}const y=e(d,[["render",p]]);export{v as __pageData,y as default};
