import{_ as e,a as o,o as r,ag as s}from"./chunks/framework.C2qfZH9T.js";const i="/assets/1-seller-dashboard.A3NpWkO4.png",a="/assets/2-product-page.BVooULR0.png",n="/assets/3-product-creation.DmbCLdno.png",l="/assets/4-create-new-product.KwDyBiiE.png",p="/assets/5-general-arrtibute.DYbVlGj8.png",d="/assets/6-Short-description.BET4Vxr7.png",c="/assets/7-description.BC_5Tvy7.png",u="/assets/8-meta-description.RQNQGPX-.png",h="/assets/9-Img.BqgB7oGa.png",g="/assets/10-customization-item.BrhrR_vt.png",m="/assets/11-price.DDYLnRkp.png",b="/assets/12-shipping.Burt1bF8.png",f="/assets/13-setting.Dumd-Uwe.png",y="/assets/14-inventory-and-category.C9u_SJuS.png",w="/assets/15-product-disapproved-state.BYJmkfPW.png",v="/assets/16-admin-product-page.TFdDJKzy.png",P="/assets/17-admin-update-status.BAE_GXI0.png",_="/assets/20-product-frontend-view.DI6lE_7e.png",k="/assets/21-search-product.C7yhIp0O.png",S="/assets/22-assign-produt-details.B-MNbYe2.png",q="/assets/23-assign-product-disapproved.BsQNw6ID.png",x="/assets/28-assign-product-store-frontend.CCANU0Fs.png",B=JSON.parse('{"title":"Product Management","description":"","frontmatter":{},"headers":[],"relativePath":"multi-vendor-marketplace/product-management.md","filePath":"multi-vendor-marketplace/product-management.md","lastUpdated":1762169668000}'),A={name:"multi-vendor-marketplace/product-management.md"};function D(C,t,I,T,E,N){return r(),o("div",null,t[0]||(t[0]=[s('<h1 id="product-management" tabindex="-1">Product Management <a class="header-anchor" href="#product-management" aria-label="Permalink to &quot;Product Management&quot;">​</a></h1><p>In Multi Vendor Marketplace, Product management is designed to give sellers complete control over their online store. It allows sellers to easily add new products, set prices, upload images, and update stock.</p><p>With easy-to-use tools, sellers can quickly edit or update products whenever needed, ensuring their listings stay accurate and attractive. Whether it’s managing a single item or hundreds of products, Multi Vendor Marketplace makes the process smooth and stress-free, so sellers can focus more on growing sales and keeping customers happy.</p><h3 id="seller-dashboard-product-management" tabindex="-1">Seller Dashboard: Product Management <a class="header-anchor" href="#seller-dashboard-product-management" aria-label="Permalink to &quot;Seller Dashboard: Product Management&quot;">​</a></h3><p>Product management is simple; sellers can add products, set details like price and stock, upload images, and quickly make them available in the marketplace.</p><p>From the seller dashboard, go to <strong>Products</strong> and click on it to open the product page.</p><img src="'+i+'" alt="Seller Dashboard"><h3 id="seller-products" tabindex="-1">Seller Products <a class="header-anchor" href="#seller-products" aria-label="Permalink to &quot;Seller Products&quot;">​</a></h3><img src="'+a+'" alt="Product Page"><p>Click on the <strong>Add New Product</strong> button at the top right corner to begin adding a new product to your store.</p><p>You can add a product in two ways:</p><ul><li><p><strong>Add New Product:</strong> Add a completely new product with your own details.</p></li><li><p><strong>Add Existing Product:</strong> Choose from existing products added by the admin.</p></li></ul><img src="'+n+'" alt="Product Creation"><h3 id="add-new-product" tabindex="-1">Add New Product <a class="header-anchor" href="#add-new-product" aria-label="Permalink to &quot;Add New Product&quot;">​</a></h3><p>To create a new product, fill in the basic details:</p><ul><li><p><strong>Product Type:</strong> Select the type of product you want to add.</p></li><li><p><strong>Attribute Family:</strong> Choose the attribute set that defines product features.</p></li><li><p><strong>SKU:</strong> Enter a unique product code for easy identification.</p></li></ul><p>Click on the <strong>Continue</strong> button to proceed further. These fields help define the structure and unique identity of your product in the store.</p><img src="'+l+'" alt="Create New Product"><h4 id="general-attribute" tabindex="-1">General Attribute <a class="header-anchor" href="#general-attribute" aria-label="Permalink to &quot;General Attribute&quot;">​</a></h4><p>In the <strong>General Attributes</strong> section, you need to enter the following details:</p><ul><li><p><strong>SKU:</strong> A unique code to identify the product.</p></li><li><p><strong>Product Number:</strong> Internal reference or product ID.</p></li><li><p><strong>Name:</strong> The product’s display name shown to customers.</p></li><li><p><strong>URL Key:</strong> SEO-friendly URL for the product page.</p></li><li><p><strong>Tax Category:</strong> Select the tax rules that apply to the product.</p></li></ul><img src="'+p+'" alt="General Attribute"><h4 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;Description&quot;">​</a></h4><p>Below is the list of fields you need to fill under the <strong>Description</strong> section:</p><ul><li><strong>Short Description:</strong> Enter a brief summary highlighting the key features of the product.</li></ul><img src="'+d+'" alt="Short Description"><ul><li><strong>Description:</strong> Provide a detailed explanation of the product, covering all important details to help customers understand it better.</li></ul><img src="'+c+'" alt="Description"><h4 id="meta-description" tabindex="-1">Meta Description <a class="header-anchor" href="#meta-description" aria-label="Permalink to &quot;Meta Description&quot;">​</a></h4><p>Below is the list of fields you need to fill under the <strong>Meta Description</strong> section:</p><ul><li><p><strong>Meta Title:</strong> Provide the main title of the product by which your product will be known.</p></li><li><p><strong>Meta Keyword:</strong> Add specific keywords to improve the product’s visibility on search engines.</p></li><li><p><strong>Meta Description:</strong> Enter a short description so the product can easily appear in search engine listings.</p></li></ul><img src="'+u+'" alt="Meta Description"><h4 id="images" tabindex="-1">Images <a class="header-anchor" href="#images" aria-label="Permalink to &quot;Images&quot;">​</a></h4><p>To add product images, click on <strong>Add Images</strong>. You can upload multiple images to showcase your product from different angles, helping customers get a better view before making a purchase.</p><img src="'+h+'" alt="Product Images"><h4 id="customizable-item" tabindex="-1">Customizable Item <a class="header-anchor" href="#customizable-item" aria-label="Permalink to &quot;Customizable Item&quot;">​</a></h4><p>A customizable item is a feature, component, or service that can be tailored to meet specific user needs, ensuring a personalized experience.</p><p>Below is the list of fields you need to provide under the <strong>Customizable Item</strong> section to allow product personalization:</p><ul><li><p><strong>Title:</strong> Provide the title of the customization by which it will be known in the product.</p></li><li><p><strong>Type:</strong> Select the type of customization input you want to offer customers.</p></li></ul><p><strong>Customization Data Types:</strong></p><ul><li><p><strong>Text:</strong> Allows users to enter a short text, such as a name or message.</p></li><li><p><strong>Textarea:</strong> Provides a larger box for longer messages or instructions.</p></li><li><p><strong>Checkbox:</strong> Customers can select multiple options from a list.</p></li><li><p><strong>Radio:</strong> Customers can select only one option from multiple choices.</p></li><li><p><strong>Select:</strong> A dropdown list with multiple choices but only one selection.</p></li><li><p><strong>Multi Select:</strong> A dropdown list where multiple options can be selected.</p></li><li><p><strong>Date:</strong> Customers can choose a specific date.</p></li><li><p><strong>DateTime:</strong> Customers can choose both date and time together.</p></li><li><p><strong>Time:</strong> Customers can select only a time value.</p></li><li><p><strong>File:</strong> Allows users to upload an image, logo, or other files for personalization.</p></li></ul><img src="'+g+'" alt="Customizable Item"><h4 id="price" tabindex="-1">Price <a class="header-anchor" href="#price" aria-label="Permalink to &quot;Price&quot;">​</a></h4><p>In this section, you need to set the pricing details of your product:</p><ul><li><p><strong>Price:</strong> Enter the regular selling price of the product.</p></li><li><p><strong>Cost:</strong> Add the actual cost of the product (used for profit tracking).</p></li><li><p><strong>Special Price:</strong> Provide a discounted price for the product.</p></li><li><p><strong>Special Price Date:</strong> Set the start and end date during which the special price will be visible on the website.</p></li></ul><img src="'+m+'" alt="Price"><h4 id="shipping" tabindex="-1">Shipping <a class="header-anchor" href="#shipping" aria-label="Permalink to &quot;Shipping&quot;">​</a></h4><p>In this section, you need to provide the product’s physical details for accurate shipping calculations:</p><ul><li><p><strong>Width:</strong> Enter the width of the product.</p></li><li><p><strong>Height:</strong> Enter the height of the product.</p></li><li><p><strong>Length:</strong> Enter the length of the product.</p></li><li><p><strong>Weight:</strong> Enter the weight of the product for shipping cost calculation.</p></li></ul><img src="'+b+'" alt="Shipping"><h4 id="settings" tabindex="-1">Settings <a class="header-anchor" href="#settings" aria-label="Permalink to &quot;Settings&quot;">​</a></h4><ul><li><p><strong>New:</strong> Enable the toggle button to feature the product as a New Product. It will then appear under the New Products section.</p></li><li><p><strong>Featured:</strong> Enable the toggle button to display the product in the Featured Products section.</p></li><li><p><strong>Visible Individually:</strong> Enable the toggle button so the product will be visible individually on the storefront.</p></li><li><p><strong>Status:</strong> Enable the toggle button to activate the product and make it available in your eCommerce store.</p></li><li><p><strong>Guest Checkout:</strong> Enable the toggle button to allow guest customers (without login) to purchase the product.</p></li></ul><img src="'+f+'" alt="Settings"><h4 id="inventories" tabindex="-1">Inventories <a class="header-anchor" href="#inventories" aria-label="Permalink to &quot;Inventories&quot;">​</a></h4><p>In this section, enter the quantity of the product available in your inventory. By default, the quantity is set to 0, which means the product will appear as <strong>Out of Stock</strong> on the storefront.</p><h4 id="category" tabindex="-1">Category <a class="header-anchor" href="#category" aria-label="Permalink to &quot;Category&quot;">​</a></h4><p>In this section, assign the product to one or more categories available in your store. Categories help organize products and make it easier for customers to browse and find them on the storefront.</p><p>You can select multiple categories to ensure better visibility of your product.</p><img src="'+y+'" alt="Inventory and Category"><h4 id="save-product" tabindex="-1">Save Product <a class="header-anchor" href="#save-product" aria-label="Permalink to &quot;Save Product&quot;">​</a></h4><p>Click on the <strong>Save Product</strong> button. This will add the product to your store, but initially, it will be in a <strong>Disapproved</strong> state.</p><img src="'+w+'" alt="Product Disapproved State"><p><strong>Note:</strong> Once the admin reviews and approves it, the status will change to <strong>Approved</strong>, and only then will the product be visible on the store frontend.</p><h3 id="admin-dashboard" tabindex="-1">Admin Dashboard <a class="header-anchor" href="#admin-dashboard" aria-label="Permalink to &quot;Admin Dashboard&quot;">​</a></h3><p>From the Admin Dashboard, the admin has the authority to approve or disapprove products created by sellers. Only approved products will appear on the storefront.</p><p>Go to <strong>Marketplace &gt;&gt; Products</strong> to view and manage all your products from the seller dashboard.</p><img src="'+v+'" alt="Admin Product Page"><p>To approve a product, follow these steps:</p><ol><li><p>Check the <strong>product checkbox</strong> for the item you want to approve.</p></li><li><p>Go to the <strong>Select Action</strong> dropdown.</p></li><li><p>Click on <strong>Update Status</strong>.</p></li><li><p>Select <strong>Approved</strong> to approve the product.</p></li></ol><img src="'+P+'" alt="Admin Update Status"><p><strong>Note:</strong> Once the product is approved, it will show as <strong>Approved</strong> on the seller’s dashboard and will also become visible on the storefront for customers.</p><h3 id="store-frontend" tabindex="-1">Store Frontend <a class="header-anchor" href="#store-frontend" aria-label="Permalink to &quot;Store Frontend&quot;">​</a></h3><img src="'+_+'" alt="Product Frontend View"><h3 id="add-existing-product" tabindex="-1">Add Existing Product <a class="header-anchor" href="#add-existing-product" aria-label="Permalink to &quot;Add Existing Product&quot;">​</a></h3><p>This feature allows sellers to quickly sell items that are already listed in the marketplace. Instead of creating a new product, sellers can add their own version with different pricing, stock, and details.</p><p>Go to the <strong>Search Bar</strong> and enter the product name to find the existing product in the marketplace.</p><img src="'+k+'" alt="Search Product"><p>The matching product will appear in the results, allowing you to select it and list it under your store with different prices and stock.</p><p>Click on <strong>Sell As Yours</strong> to create your own version of the existing product.</p><h4 id="product-details" tabindex="-1">Product Details <a class="header-anchor" href="#product-details" aria-label="Permalink to &quot;Product Details&quot;">​</a></h4><p>In this section, set all the necessary information for your version of the existing product:</p><ul><li><p><strong>Condition:</strong> Specify the condition of the product (New, Old).</p></li><li><p><strong>Price:</strong> Enter the selling price you want to offer for this product.</p></li><li><p><strong>Stock:</strong> Add the available quantity of the product in inventory.</p></li><li><p><strong>Description:</strong> Provide a brief description of the product.</p></li><li><p><strong>Images:</strong> Upload product images to showcase the item.</p></li><li><p><strong>Video:</strong> Optionally, add a video to give customers a better view of the product.</p></li></ul><img src="'+S+'" alt="Assign Product Details"><p>Click on the <strong>Save</strong> button at the top right corner to add the product to your store, but initially, it will be in a <strong>Disapproved</strong> state.</p><img src="'+q+'" alt="Assign Product Disapproved"><p><strong>Note:</strong> Once the admin reviews and approves it, the status will change to <strong>Approved</strong>, and only then will the product be visible on the store frontend.</p><h3 id="store-frontend-1" tabindex="-1">Store Frontend <a class="header-anchor" href="#store-frontend-1" aria-label="Permalink to &quot;Store Frontend&quot;">​</a></h3><img src="'+x+'" alt="Assign Product Store Frontend">',88)]))}const z=e(A,[["render",D]]);export{B as __pageData,z as default};
